mapper.insert:
insert into freeboard (
	title,
	writer,
	content
)values (
#{title},
'admin',
#{content}

) on duplicate key update 
title = #{title},
writer = 'admin',
content = #{content}

mapper.delete:
delete
from
	freeboardreply
where
	reply_id = #{reply_id}
	and reply_password = #{reply_password}

delete
from
	freeboard
where
	id = #{id}
	and password = #{password}
